(* Construire une liste avec `::` *)
let ma_liste = 2::3::1::[];;

(* C'est bien la même chose *)
l = ma_liste;; (* true *)

(* Pattern matching sur les listes *)
let ma_fonction l =
  match l with
  | [] -> print_string "La liste est vide"
  | x :: tl -> print_string "La liste contient 1 élément et plus"
